import { Component, EventEmitter, Output, TemplateRef, input, viewChild } from '@angular/core';
import { IconComponent } from 'fancy-ui-core';

@Component({
  selector: 'fui-app-bar-action',
  standalone: true,
  imports: [IconComponent],
  template: `
    <ng-template #template>
      <a class="w-12 h-12 flex justify-center relative" (click)="_onClick()" role="nonMobileAppBarAction" [attr.aria-label]="label()">
        <div class="absolute z-0 w-12 h-12 rounded-xl transition-all duration-500 hover:bg-hover"></div>
        <fui-icon class="h-6 w-6 self-center" [name]="iconName()"></fui-icon>
      </a>
    </ng-template>
    <ng-template #mobileTemplate>
      <a class="px-2 py-2 flex cursor-pointer text-gray font-light hover:bg-hover rounded" (click)="_onClick()" [attr.data-testid]="label()">
        <div class="self-center text-xs font-semibold px-2">{{label()}}</div>
        <div class="h-6 w-6">
            <fui-icon [name]="iconName()"></fui-icon> 
        </div>
      </a>
    </ng-template>
  `
})
export class AppBarActionComponent {
  /** The name of the icon to show for this action in the app bar. */
  iconName = input('default');

  /** The label of the action to show in the action dropdown in case of mobile screen layout. */
  label = input('');

  /** An event called if the action is tapped. */
  @Output() tap: EventEmitter<void> = new EventEmitter<void>();

  // The templates used by the app bar to render this action
  _template = viewChild<TemplateRef<unknown>>('template');
  _mobileTemplate = viewChild<TemplateRef<unknown>>('mobileTemplate');

  _onClick() {
    this.tap.next();
  }
}

import { Component, contentChildren, inject, input } from '@angular/core';
import { Location, NgTemplateOutlet, SlicePipe } from '@angular/common';
import { fromEvent, skip, Subscription } from 'rxjs';
import { AppBarActionComponent } from './app-bar-action.component';
import { IconComponent } from 'fancy-ui-core';

@Component({
  selector: 'fui-app-bar',
  standalone: true,
  imports: [NgTemplateOutlet, IconComponent, SlicePipe],
  templateUrl: './app-bar.component.html'
})
export class AppBarComponent {
  /**
   * Title within the AppBar.
   */
  title = input('View Title');

  /**
   * Flag to indicate weather the back button shall be shown.
   */
  showBackButton = input(false);

  // The app bar actions
  _appBarActions = contentChildren(AppBarActionComponent)

  private _location = inject(Location);

  // State to save if actions dropdown is open
  public _viewActionsDropdownIsOpen = false;
  private _documentClickSubscription: Subscription | null = null;

  _openActionsDropdown(): void {
    this._viewActionsDropdownIsOpen = true;
    if(!this._documentClickSubscription) {
      this._documentClickSubscription = fromEvent(document, 'click').pipe(skip(1)).subscribe(() => this._closeActionsDropdown() );
    }
  }

  _closeActionsDropdown(): void {
    this._viewActionsDropdownIsOpen = false;
    this._documentClickSubscription?.unsubscribe();
    this._documentClickSubscription = null;
  }

  _navigateBack(): void {
    this._location.back();
  }
}

