import { Component, TemplateRef, input, viewChild, contentChild } from '@angular/core';
import { CardHeaderComponent } from 'fancy-ui-content';
import { DatasetHelpers } from './dataset.helpers';
import { NgTemplateOutlet } from '@angular/common';

@Component({
  selector: 'fui-dataset-cardheader',
  standalone: true,
  imports: [NgTemplateOutlet, CardHeaderComponent],
  template: `
    <ng-template #template let-data let-onMoreTap="more">
      <fui-card-header [titleTemplateRef]="titleTemplateWrapper" [subtitleTemplateRef]="subtitleTemplateWrapper" [imageSrc]="getImageSrc(data)" [showMore]="onMoreTap" (moreTap)="onMoreTap(data)">
        <ng-template #titleTemplateWrapper>
          <ng-container *ngTemplateOutlet="_titleTemplate() ?? null; context: { $implicit: data }" />
        </ng-template>
        <ng-template #subtitleTemplateWrapper>
          <ng-container *ngTemplateOutlet="_subtitleTemplate() ?? null; context: { $implicit: data }" />
        </ng-template>
      </fui-card-header>
    </ng-template>
  `
})
export class DatasetCardheaderComponent {
  imageSrcKeyPath = input<string | null>(null);

  _template = viewChild<TemplateRef<unknown>>('template');

  _titleTemplate = contentChild<TemplateRef<unknown>>('titleTemplate');
  _subtitleTemplate = contentChild<TemplateRef<unknown>>('subtitleTemplate');

  getImageSrc(data: unknown): string | null {
    const keyPath = this.imageSrcKeyPath();
    if(!keyPath) return null;
    return DatasetHelpers.getKeyPathValue(data, keyPath);
  }
}

import { Component, EventEmitter, Output, computed, contentChild, contentChildren, effect, input } from '@angular/core';
import { NgTemplateOutlet } from '@angular/common';
import { DatasetItemBase } from './dataset-item.base';
import { DatasetCardheaderComponent } from './dataset-cardheader.component';

import { CardComponent, CardContentComponent } from 'fancy-ui-content';
import { IconComponent } from 'fancy-ui-core';

@Component({
  selector: 'fui-dataset',
  standalone: true,
  imports: [NgTemplateOutlet,  CardComponent, CardContentComponent, IconComponent],
  templateUrl: './dataset.component.html'
})
export class DatasetComponent {
  dataSource = input<Array<unknown>>([]);

  @Output()
  moreTap = new EventEmitter<unknown>();

  onMoreTap: (value: unknown) => void;

  _datasetItems = contentChildren(DatasetItemBase);
  _datasetCardHeader = contentChild(DatasetCardheaderComponent);

  _datasetTableItems = computed(() => this._datasetItems().filter(item => item.target() === 'Table' || item.target() === 'TableAndCard'));
  _datasetCardItems = computed(() => this._datasetItems().filter(item => item.target() === 'Card'|| item.target() === 'TableAndCard'));

  constructor() {
    this.onMoreTap = this.onMoreTapInternal.bind(this);
  }

  onMoreTapInternal(data: unknown) {
    this.moreTap.emit(data);
  }
}

components.templateData:
@if(dataSource(); as data) {
    <div class="md:hidden grid gap-component">
        @for(cardData of data; track $index) {
            <fui-card>
                @if(_datasetCardHeader(); as cardHeader) {
                    <ng-container [ngTemplateOutlet]="cardHeader._template() ?? null"
                                  [ngTemplateOutletContext]="{ $implicit: cardData, more: onMoreTap}"/>
                }
                <fui-card-content>
                    <div class="grid grid-cols-12">
                        @for(item of _datasetCardItems(); track $index) {
                            <ng-container [ngTemplateOutlet]="item.cardTemplate ?? null"
                                          [ngTemplateOutletContext]="{ $implicit: cardData }" />
                        }
                    </div>
                </fui-card-content>
            </fui-card>
        }
    </div>

    <table class="w-full hidden md:table">
        <thead>
            <tr class="border-b">
                @for(item of _datasetTableItems(); track $index) {
                    <th class="text-left">{{ item.label() }}</th>
                }
                @if(moreTap.observers.length) {
                    <th class="text-left"></th> 
                }
            </tr>
        </thead>
        <tbody>
            @for(rowData of data; track $index) {
                <tr class="even:bg-shade">
                    @for(item of _datasetTableItems(); track $index) {
                        <td>
                            <ng-container [ngTemplateOutlet]="item.tableTemplate ?? null"
                                          [ngTemplateOutletContext]="{ $implicit: rowData }" />
                        </td>
                    }
                    @if(moreTap.observers.length) {
                        <td class="flex justify-end min-h-hover">
                            <a (click)="onMoreTap(rowData)" class="w-hover flex justify-center items-center">
                                <div class="absolute w-hover w-hover h-hover rounded-xl transition-all duration-500 hover:bg-hover"></div>
                                <fui-icon class="h-icon w-icon" name="matMoreVertOutline" />
                            </a>
                        </td>
                    }
                </tr>
            }
        </tbody>
    </table>
}

